        operators = {"+", "-", "*", "/"}

        for token in tokens:
            if token in operators:
                b = stack.pop()
                a = stack.pop()
                if token == '+':
                    stack.append(a + b)
                elif token == '-':
                    stack.append(a - b)
                elif token == '*':
                    stack.append(a * b)
                elif token == '/':
                    # Truncates toward zero, e.g., int(6 / -132) = 0
                    stack.append(int(a / b))
            else:
                stack.append(int(token))

        return stack[0]

